import { FC } from 'react';
import {
  Icon as ChakraIcon,
  IconProps as ChakraIconProps,
} from '@chakra-ui/react';

export enum IconName {
  twitter = 'twitter',
  github = 'github',
  linkedIn = 'linkedIn',
  logo = 'logo',
}

interface IconProps extends ChakraIconProps {
  name: IconName;
}

export const Icon: FC<IconProps> = ({ name, ...props }) => (
  <ChakraIcon color="brand.grey" {...props}>
    {getIconSvg(name)}
  </ChakraIcon>
);

const getIconSvg = (name: IconName) => {
  switch (name) {
    case IconName.twitter:
      return Twitter;
    case IconName.github:
      return Github;
    case IconName.linkedIn:
      return LinkedIn;
    case IconName.logo:
      return Logo;
    default:
      return null;
  }
};

